  // Method to dynamically set the count of technologies in facts
  updateTechnologiesCount() {
    const techCount = this.languages.length; // Count the languages
    this.facts = this.facts.map((fact) =>
      fact.description === 'Technologies worked with'
        ? { ...fact, title: techCount.toString() }
        : fact
    );
  }

